import { useState } from 'react';


function App() {
  // stateful component
  const [clicks, setClicks] = useState({
    left: 0,
    right: 0
  })

  const increaseLeft = () => {
    const newClicks = {
      ...clicks,
      left: clicks.left + 1
    }
    setClicks(newClicks)
  }

  return (
    <>
      {clicks.left}
      <button onClick={increaseLeft}>Left</button>
      <button>Right</button>
      {clicks.right}
    </>
  );
}

export default App;
