import { useState } from 'react';


function Button({callback, text}) {
  return (
    <button onClick={callback}>{text}</button>
  )
}

function App() {
  // stateful component
  const [counter, setCounter] = useState(0);

  const handleClick = (value) => () => setCounter(value);

  return (
    <>
      {counter}
      <Button callback={handleClick(0)} text="Reset" />
      <Button callback={handleClick(counter + 1)} text="Increase" />
    </>
  );
}

export default App;
