import { useState } from 'react';


const ClicksHistory = ({clicks}) => {
  if (clicks.length === 0) {
    return (
    <>
      <h1>Press the buttons to start the application</h1>
    </>
    )
  }

  return (
    <>
      <h1>{clicks.join("-")}</h1>
    </>
  )
}

const Button = ({eventHandler, text, value}) => {
  return (
    <>
      <button onClick={eventHandler(value)}>{text}</button>
    </>
  )
}

function App() {
  // stateful component
  const [counterLeft, setCounterLeft] = useState(0);


  const handleLeftClick = (value) => {
    setCounterLeft(value);
  }

  return (
    <>
      {counterLeft}

      <Button eventHandler={handleLeftClick(0)} text="Reset left" /> 
      <Button eventHandler={handleLeftClick(1000)} text="Set left to 1000" /> 

    </>
  );
}

export default App;
