import { useState } from 'react';


function App() {
  // stateful component
  const [counter, setCounter] = useState(0);

  // event handlers
  const resetCounter = () => {
    setCounter(0);
  }

  const increaseCounter = () => {
    setCounter(counter + 1)
  }

  return (
    <>
      <p>Counter: {counter}</p>
      <button onClick={resetCounter}>Reset</button>
      <button onClick={increaseCounter}>Increase</button>
    </>
  );
}

export default App;
