import { useState } from 'react';


const clicksHistory = ({clicks}) => {
  if (clicks.length === 0) {
    <>
      <h1>Press the buttons to start the application</h1>
    </>
  }

  return (
    <>
      <h1>{clicks.join("-")}</h1>
    </>
  )
}

function App() {
  // stateful component
  const [counterLeft, setCounterLeft] = useState(0);
  const [counterRight, setCounterRight] = useState(0);
  const [clicks, setClicks] = useState([]);

  const handleLeftClick = () => {
    setCounterLeft(counterLeft  + 1);
    setClicks(clicks.concat("L"));
  }

  const handleRightClick = () => {
    setCounterRight(counterRight + 1);
    setClicks(clicks.concat("R"))
  }

  return (
    <>
      {counterLeft}
      <button onClick={handleLeftClick}>Left</button>
      <button onClick={handleRightClick}>Right</button>
      {counterRight}
      <h1>{clicks.join("-")}</h1>
    </>
  );
}

export default App;
