import { useState } from 'react';

function App() {
  // stateful component
  const [counter, setCounter] = useState(0);

  const handleClick = (value) => () => setCounter(value);

  return (
    <>
      {counter}
      <button onClick={handleClick(0)}>Reset</button> 
      <button onClick={handleClick(counter + 1)}>Increase</button> 

    </>
  );
}

export default App;
